<div *ngIf="functionality">
  <h2>{{ functionality.name }}</h2>
  <p>Status: {{ functionality.status }}</p>
  <ul>
    <li *ngFor="let task of functionality.tasks">
      <span>{{ task.name }}</span>
      <button (click)="showTaskDetails(task.ID)">Pokaż szczegóły</button>
      <button (click)="deleteTask(task.ID)">Usuń</button>
    </li>
  </ul>
</div>

<div *ngIf="functionalities.length > 0">
  <h2>Inne funkcjonalności</h2>
  <ul>
    <li *ngFor="let func of functionalities">
      <span>{{ func.name }}</span>
      <button (click)="updateFunctionalityStatus(func)">Aktualizuj status</button>
    </li>
  </ul>
</div>

<button (click)="addTask()">Dodaj zadanie</button>
