import { Component, OnInit } from '@angular/core';
import { ProjectInterface } from 'src/app/interfaces/project.interface';
import { ProjectService } from 'src/app/services/project.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-user-projects',
  templateUrl: './user-projects.component.html',
  styleUrls: ['./user-projects.component.scss'],
})
export class UserProjectsComponent implements OnInit {
  projects: ProjectInterface[] = [];
  project!: ProjectInterface;

  constructor(private projectService: ProjectService, private router: Router) {}

  ngOnInit() {
    this.projectService
      .getProjects()
      .subscribe((project: ProjectInterface[]) => {
        this.projects = project;
      });
    this.project = this.projects[0];
  }

  showFunctionalities() {
    this.router.navigate(['/functionalities']);
  }
}
<div class="max-w-[1200px] mt-4 mx-6 xl:mx-auto">
  <div class="px-6 py-4 bg-white my-2 rounded-xl relative border shadow-sm">
    <ul>
      <li>
        <h1 class="text-xl">{{ project.name }}</h1>
      </li>
      <li class="text-gray-400 text-sm my-2">{{ project.description }}</li>
    </ul>

    <div class="absolute right-4 top-4 rounded-full p-1">
      <div class="flex gap-2 text-xl">
        <button (click)="showFunctionalities()">INFO</button>
      </div>
    </div>
  </div>
</div>
