import { Component, OnInit } from "@angular/core";
import { TaskInterface } from "src/app/interfaces/task.interface";
import { TaskService } from "src/app/services/task.service";
import { Priority } from "src/app/enums/priority.enum";
import { WorkStatus } from "src/app/enums/workStatus.enum";
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from "@angular/forms";
import { Router } from "@angular/router";
import { FunctionalityService } from "src/app/services/functionality.service";
import { FunctionalityInterface } from "src/app/interfaces/functionality.interface";

@Component({
  selector: "app-task-form",
  templateUrl: "./task-form.component.html",
  styleUrls: ["./task-form.component.css"],
})
export class TaskFormComponent implements OnInit {
  tasks: TaskInterface[] = [];
  taskForm!: FormGroup;
  workStatusValues = Object.values(WorkStatus);
  priorityValues = Object.values(Priority);
  functionalityList: FunctionalityInterface[] = [];

  constructor(
    private formBuilder: FormBuilder,
    private taskService: TaskService,
    private functionalityService: FunctionalityService,
    private router: Router
  ) {}

  ngOnInit(): void {
    const currentDate = new Date().toISOString().split("T")[0];
    const addedDateControl = new FormControl({
      value: currentDate,
      disabled: true,
    });

    this.taskForm = this.formBuilder.group({
      name: ["", Validators.required],
      description: ["", Validators.required],
      priority: [Priority.Low, Validators.required],
      projectName: ["", Validators.required],
      owner: ["", Validators.required],
      status: [WorkStatus.Todo, Validators.required],
      addedDate: addedDateControl,
      startDate: [""],
      endDate: [""],
      timeSpent: [""],
      functionality: ["", Validators.required],
    });

    this.taskService.getTasks().subscribe(
      (tasks: TaskInterface[]) => {
        this.tasks = tasks;
      },
      (error) => console.log(error)
    );

    this.functionalityService.getFunctionalities().subscribe(
      (functionalities: FunctionalityInterface[]) => {
        this.functionalityList = functionalities;
      },
      (error) => console.log(error)
    );
  }

  addTask() {
    const currentDate = new Date().toISOString().split("T")[0];
    const addedDateControl = new FormControl({
      value: currentDate,
      disabled: true,
    });

    const addedDateValue =
      addedDateControl.value !== null ? addedDateControl.value : currentDate;

    const selectedFunctionalityId = this.taskForm.value.functionality;

    const selectedFunctionality = this.functionalityList.find(
      (f) => f.ID === selectedFunctionalityId
    );

    if (!selectedFunctionality) {
      return; // Dodaj odpowiednie obsłużenie błędu, jeśli funkcjonalność nie zostanie znaleziona
    }

    const newTask: TaskInterface = {
      ID: Date.now().toString(),
      name: this.taskForm.value.name,
      description: this.taskForm.value.description,
      priority: this.taskForm.value.priority,
      functionalityID: selectedFunctionality.ID,
      functionality: selectedFunctionality,
      state: this.taskForm.value.status,
      addedDate: new Date(addedDateValue),
      startDate: this.taskForm.value.startDate,
      endDate: this.taskForm.value.endDate,
      assignedUser: this.taskForm.value.owner,
    };

    this.taskService.createTask(newTask).subscribe(
      (task: TaskInterface) => {
        console.log(task);
        if (!selectedFunctionality.tasks) {
          selectedFunctionality.tasks = []; // Inicjalizuj tablicę zadaniami, jeśli nie istnieje
        }
        selectedFunctionality.tasks.push(task); // Dodaj zadanie do listy zadań funkcjonalności
        this.router.navigateByUrl("/functionalities");
      },
      (error) => console.log(error)
    );
  }
}
<div>
  <h1>Add Task</h1>
  <form [formGroup]="taskForm" (ngSubmit)="addTask()">
    <div>
      <label for="name">Name:</label>
      <input type="text" id="name" formControlName="name" required>
    </div>
    <div>
      <label for="description">Description:</label>
      <textarea id="description" formControlName="description" required></textarea>
    </div>
    <div>
      <label for="priority">Priority:</label>
      <select id="priority" formControlName="priority" required>
        <option *ngFor="let priority of priorityValues" [value]="priority">{{ priority }}</option>
      </select>
    </div>
    <div>
      <label for="projectName">Project Name:</label>
      <input type="text" id="projectName" formControlName="projectName" required>
    </div>
    <div>
      <label for="owner">Owner:</label>
      <input type="text" id="owner" formControlName="owner" required>
    </div>
    <div>
      <label for="status">Status:</label>
      <select id="status" formControlName="status" required>
        <option *ngFor="let status of workStatusValues" [value]="status">{{ status }}</option>
      </select>
    </div>
    <div>
      <label for="addedDate">Added Date:</label>
      <input type="text" id="addedDate" formControlName="addedDate" [disabled]="true">
    </div>
    <div>
      <label for="startDate">Start Date:</label>
      <input type="text" id="startDate" formControlName="startDate">
    </div>
    <div>
      <label for="endDate">End Date:</label>
      <input type="text" id="endDate" formControlName="endDate">
    </div>
    <div>
      <label for="timeSpent">Time Spent:</label>
      <input type="text" id="timeSpent" formControlName="timeSpent">
    </div>
    <div>
      <label for="functionality">Functionality:</label>
      <select id="functionality" formControlName="functionality" required>
        <option *ngFor="let functionality of functionalityList" [value]="functionality.ID">{{ functionality.name }}</option>
      </select>
    </div>
    <div>
      <button type="submit">Add Task</button>
    </div>
  </form>
</div>
